// fontawesome for icons
$('head').append($("<script src='https://kit.fontawesome.com/29c8b6a8a8.js' crossorigin='anonymous'></script>"))

// grab table containing posts
var tbody = $('#cphBody_tblThread tbody');

// groups <tr>s from chatboard correctly considering they are usually 3 but sometimes have attachments. (gotta be a better way)
var buildPostsArray = function(tdArray) {
	var result = [[]]
	for (var i = 0; i< tdArray.length; i ++) {
		// last sub array in result
		var currentArray = result[result.length - 1]
		// add tr to currentArray
		currentArray.push(tdArray[i])
		if (currentArray.length === 3){
			var currentTr = currentArray[2]
			// check if the 3rd element in the group is an attachment row, if so we add the next row and start a new group
			if (currentTr.innerHTML.includes('<span style="color:Red;">')) {
				currentArray.push(tdArray[i+1])
				result.push([])
				i ++
			// if not just start a new group
			} else {
				result.push([])
			}
		}
	}
	return result
}

// put elements into subarrays of specific length
function chunkArray(array, size) {
    let result = []
    for (let i = 0; i < array.length; i += size) {
        let chunk = array.slice(i, i + size)
        result.push(chunk)
    }
    return result
}

function flipTable(tbody) {
	var posts = $('tr', tbody).get();

	// var reverse_posts = chunkArray(posts, 3).reverse().flat();
	var reverse_posts = buildPostsArray(posts).reverse().flat();
	tbody.append(reverse_posts);
}

// normally 3 elements (<tr>) per message, 4 with attachmenst so we only add the sort button for >3
if (tbody.children().get().length > 3) {

	// add button
	tbody.append($("<i id='reverseOrderButton' class='fas fa-sort flyCarolButton'/></i>"));

	$("#reverseOrderButton").click(function() {
		flipTable(tbody);
	});
}

