// wait for load in iframe
var waitForIframeEl = function(selector, callback) {
	// console.log(window.frames[0])
  try {
  	window.frames[0].$(selector).length
  	// console.log("firing callback")
    callback();
  } catch(err) {
  	console.log("Error in waitForIframeEl: " + err)
    setTimeout(function() {
    	// console.log("waitForIframeEl setting timeout")
      waitForIframeEl(selector, callback);
    }, 100);
  }
};

// wait for load in doc
var waitForEl = function(selector, callback) {
  if ($(selector).length) {
  	console.log("firing callback")
    callback();
  } else {
    setTimeout(function() {
    	console.log("setting timeout")
      waitForEl(selector, callback);
    }, 100);
  }
};

// ensures styling on iframe is reset if auto refer errors out
var resetIframe = function () {
	setTimeout(function(){
		try {
			window.frames[0].CloseWindow()
		} catch(err) {
		}
		iframeContainer.removeClass("headless")
		$("#addWlReferralButton").html("Warm Line Referral")
	}, 5000);
}

// check for name in place
var isNameSet = function () {
	return !!$("#cphBody_txtCaller")[0].value
}

// scroll to name field and highlight
var alertNameMissing = function () {
	var el = $("#cphBody_txtCaller")
	$("#cphBody_txtCaller").addClass("alertNameMissing")

	var elOffset = el.offset().top;
	var elHeight = el.height();
	var windowHeight = $(window).height();
	var offset;

	if (elHeight < windowHeight) {
		offset = elOffset - ((windowHeight / 2) - (elHeight / 2));
	}
	else {
		offset = elOffset;
	}

	document.documentElement.scrollTo(null, offset)

	$('#cphBody_txtMessageBody').focus()
}

$("#cphBody_txtCaller").on("change", function () {
	if (isNameSet()) {
		$("#cphBody_txtCaller").removeClass("alertNameMissing")
	}
})

